import React from 'react';
import axios from 'axios';
import SearchSuggestions from './SearchSuggestions/SearchSuggestions';
import Pill from './Pill/Pill';
import './App.css';


const apiKey = '9adcdf48'
class App extends React.Component {

  constructor() {
    super();
    this.state = {
      inputValue: '',
      searchSuggestions: [],
      selectedMovies: []
    }
    this.getMovies = this.debounce(this.getMovies.bind(this), 300);
  }


  inputChangeHandler = (e) => {
    let inputValue = e.target.value;
    this.setState({
      inputValue
    }, () => {
      this.getMovies();
    });
  }

  getMovies = () => {
    let { inputValue } = this.state;
    axios.get(`http://www.omdbapi.com/?apikey=`+apiKey+`&s=`+inputValue)
    .then((res)=>{
      let searchSuggestions = res.Search;
      this.setState({
        searchSuggestions
      });
    });
  }

  updateSelectedMovies = (selectedMovie) => {
    let selectedMovies = [...this.state.selectedMovies ];
    let moviePresent = false;
    selectedMovies.forEach((movie) => {
      if (movie.imdbID === selectedMovie.imdbID) {
        moviePresent = true;
      }
    });
    if (moviePresent || selectedMovies.length === 5) {
      return;
    }
    selectedMovies.push(selectedMovie)
    this.setState({
      selectedMovies
    })
  }

  deleteSelectedMovies = (index) => {
    let { selectedMovies } = this.state;
    selectedMovies.splice(index, 1);
    this.setState({
      selectedMovies
    });
  }

  clearSuggestions = () => {
    let searchSuggestions = [];
    this.setState({
      searchSuggestions
    })
  }

  debounce = (func, delay) => {
    let debounceTimer
    return function () {
      const context = this
      const args = arguments
      clearTimeout(debounceTimer)
      debounceTimer
        = setTimeout(() => func.apply(context, args), delay)
    }
  }

  render() {
    let { inputValue, searchSuggestions, selectedMovies } = this.state;
    return (
      <div className="App" onClick={this.clearSuggestions}>
        <div className="container" onClick={(e) => e.stopPropagation()}>
          <Pill selectedMovies={selectedMovies} deleteSelectedMovies={this.deleteSelectedMovies}
            inputValue={inputValue} inputChangeHandler={this.inputChangeHandler} />
          <SearchSuggestions searchSuggestions={searchSuggestions} updateSelectedMovies={this.updateSelectedMovies} />
        </div>
      </div>
    );
  }
}

export default App;
