import React from 'react';
import axios from 'axios';
import './App.css';

const apiKey = '9adcdf48'
class App extends React.Component {

  constructor() {
    super();
    this.state = {
      inputValue: ''
    }
  }

  componentDidMount() {
    axios.get(`http://www.omdbapi.com/?apikey=`+apiKey+`&s=batman`)
    .then((res)=>{
      console.log(res);
    })
  }

  inputChangeHandler = (e) => {
    let inputValue = e.target.value;
    this.setState({
      inputValue
    })
  }
  render() {
    let {inputValue} = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <input type = "text" value ={inputValue} onChange= {this.inputChangeHandler}></input>

        </header>
      </div>
    );
  }  
}

export default App;
