      },
      {
        "Title": "Rise of the Black Bat",
        "Year": "2012",
        "imdbID": "tt2205589",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BMjMwMTQ2OTU2MF5BMl5BanBnXkFtZTcwMDI0OTU4OA@@._V1_SX300.jpg"
      }
    ]
    this.setState({
      searchSuggestions
    });
    // axios.get(`http://www.omdbapi.com/?apikey=`+apiKey+`&s=`+inputValue)
    // .then((res)=>{
