  }

  updateSelectedMovies = (movie) => {
    let { selectedMovies } = this.state;
    if(selectedMovies.length ===5) {
      return;
    }
    if(!selectedMovies.includes(movie)){
      selectedMovies.push(movie);
    }
    this.setState({
      selectedMovies
    })
  }

  deleteSelectedMovies = (index) => {
    let {selectedMovies} = this.state;
    selectedMovies.splice(index,1);
    this.setState({
      selectedMovies
    })
  }

  debounce = (func, delay) => {
    let debounceTimer
    return function () {
      const context = this
      const args = arguments
      clearTimeout(debounceTimer)
      debounceTimer
        = setTimeout(() => func.apply(context, args), delay)
    }
  }

  render() {
    let { inputValue, searchSuggestions, selectedMovies } = this.state;
    return (
      <div className="App">
        <div className="AutoSuggest">
          {selectedMovies.map((movie, index) => {
            return (
              <div className="Pill" key={movie.imdbID}>
                <div className= "PillText">
                  {movie.Title}
                </div>
                <div className="PillClose" onClick={() => this.deleteSelectedMovies(index)}>X</div>
              </div>
            )
          })}
          <input className="textInput" type="text" value={inputValue} onChange={this.inputChangeHandler}></input>
        </div>
        <SearchSuggestions searchSuggestions={searchSuggestions} updateSelectedMovies={this.updateSelectedMovies} />
      </div>
    );
  }
}

export default App;
